import React, { Component } from 'react';

import './Navbar.css';

export default class Navbar extends Component {

    componentDidMount() {
        const open = document.getElementById('hamburger');
        let changeIcon = true;

        open.addEventListener('click', function () {
            const overlay = document.querySelector('.overlay');
            const nav = document.querySelector('nav');
            let icon = document.querySelector('.menu-toggle i');

            overlay.classNameList.toggle('menu-open');
            nav.classNameList.toggle('menu-open');

            if (changeIcon) {
                icon.classNameList.remove('fa-bars');
                icon.classNameList.add('fa-times');

                changeIcon = false;
            } else {
                icon.classNameList.remove('fa-times');
                icon.classNameList.add('fa-bars');
                changeIcon = true;
            }
        });

    }

    render() {
        return (
            <div>
                <header>
                    <div className='menu-toggle' id='hamburger'>
                        {/* <i className='fas fa-bars'></i> */}
                        <i className='fas fa-skull-crossbones'></i>
                    </div>
                    <div className='overlay'></div>
                    <div className='container'>
                        <nav>
                            <h1 className='brand'>
                                <a href='index.html'>
                                    Ro<span>am</span>in
                                </a>
                            </h1>
                            <ul>
                                <li>
                                    <a href='#'>Brando</a>
                                </li>
                                <li>
                                    <a href='#'>Max</a>
                                </li>
                                <li>
                                    <a href='#'>B.A.S.E.</a>
                                </li>
                                <li>
                                    <a href='#'>Noms</a>
                                </li>
                            </ul>
                        </nav>
                    </div>
                </header>
            </div>
        );
    }
}
